import React, { useState, useEffect } from 'react'
import Persons from './components/Persons'
import Filter from './components/Filter'
import PersonForm from './components/PersonForm'
import personService from './services/persons'

const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filterValue, setFilter] = useState('')

  useEffect(() => {
    personService
      .getAll()
      .then(initialNames => {
        setPersons(initialNames)
      })
  }, [])

  const addName = (event) => {
    event.preventDefault()
    const nameObject = {
      id: persons.length + 1,
      name: newName,
      number: newNumber,
    }

    if(persons.some(person => person.name === nameObject.name)){
      if (window.confirm(`${nameObject.name} is already added to phonebook, replace the old number with a new one?`)){
        const existedperson = persons.find(person => person.name === nameObject.name)
        const changedNote = { ...existedperson, number: nameObject.number }
        personService
          .update(nameObject.id, changedNote)
          .then(returnedNote => {
            setPersons(returnedNote)
      })
      };
    } else {
      setPersons(persons.concat(nameObject))
      setNewName('')
      setNewNumber('')

      personService
      .create(nameObject)
      .then(returnedNote => {
        setPersons(persons.concat(returnedNote))
      })

  }};



  const handleNameChange = (event) => {
    setNewName(event.target.value);
  };

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value);
  };


  const handlefilterChange = (event) => {
    setFilter(event.target.value);
  };

  const handleDelete = person => {
    personService
    .remove(person)
    .then(response => {
      window.confirm(`Delete ${person.name}?`);
      personService
      .getAll()
      .then(initialNames => {
        setPersons(initialNames)
      })
    });

  } 

/*   const handleDelete = person => {
    axios
    .delete(`http://localhost:3001/persons/${person.id}`)
    .then(response => {
      window.confirm(`Delete ${person.name}?`);
      personService
      .getAll()
      .then(initialNames => {
        setPersons(initialNames)
      })
    });

  }  */
  return (
    <div>
      <h2>Phonebook</h2>
      <Filter  value={filterValue}
          onChange={handlefilterChange}/>
      <PersonForm onSubmit={addName} valueName={newName} onChangeName={handleNameChange} valueNumber={newNumber} onChangeNumber={handleNumberChange}/>

      <h2>Numbers</h2>
      <Persons persons = {persons} filterValue = {filterValue} onDelete ={handleDelete}/>
    </div>
  )
}

export default App;
